<template>
	<view class="input-swiper">
		<input type="text" v-model="choseItem" class="input" />
		<view @click.stop="onUpperClick" class="iconfont iconarrowBottom-fill right-arrow" :class="{isRotate: isRotate}">

		</view>

		<view class="item-swiper">
			<text v-for="(v,k) in innerList" :key="k" @click.stop="changeItem(k)" class="input">{{v.value}}</text>
		</view>
	</view>
</template>

<script>
	export default {
		props: {
			list: {
				type: Array,
				default: []
			},
			showItemNum: {
				type: Number,
				default: 5
			}
		},
		data() {
			return {
				innerList: this.list,
				choseItem: '',
				isRotate:false
			}
		},
		methods: {
			changeItem(index) {
				this.choseItem = this.innerList[index].value;
				this.$emit("change", this.innerList[index]);
			},
			onUpperClick(){
				this.isRotate=true;
			}
		}
	}
</script>

